import React, { useState } from 'react'
import { Link } from 'react-router-dom'

export default function Signup() {
    const [credentials, setcredentials] = useState({name :"",email:"", password:"", geolocation: " "})
    const handleSubmit = async(e)=>{
        e.preventDefault();
        const response = await fetch("http://localhost:4000/api/createuser",{
            method:'POST',
            header:{
                'Content-Type': 'application/json'
            },
            body:JSON.stringify({name :credentials.name,email:credentials.email, password:credentials.password, location: credentials.geolocation})
        });
        const json = await response.json()
        console.log(json)
        if(!json.success){
            alert
        }
    }
    const  onChange = (event)=>{
        setcredentials ({...credentials,[event.target.name]: event.target.value})

    }
  return (
    <>
    <div className="container">
    <form onSubmit = {handleSubmit}>
    <div className="mb-3">
    <label htmlfor="InputName" className="form-label">Name</label>
    <input type="text" className="form-control" name = "name" value = {credentials.name} onChange= {onChange}/>

  </div>
  <div className="mb-3">
    <label htmlfor="InputEmail1" className="form-label">Email address</label>
    <input type="email" className="form-control" name="email" value ={credentials.email}onChange= {onChange} id="exampleInputEmail1" aria-describedby="emailHelp"/>
    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div className="mb-3">
    <label htmlfor="InputPassword1" className="form-label">Password</label>
    <input type="password" className="form-control" name = "password" value ={credentials.password} onChange= {onChange} id="exampleInputPassword1"/>
  </div>
  <div className="mb-3">
    <label htmlfor="InputPassword1" className="form-label">Address</label>
    <input type="text" className="form-control" name = "geolocation" value ={credentials.geolocation}onChange= {onChange} id="exampleInputPassword1"/>
  </div>
  <button type="submit" className="btn btn-primary">Submit</button>
  <Link to = "/login" className ="m-3 btn btn-danger">Already User</Link>
</form>
</div>

    </>
  )
}
