import React ,{useEffect, useState,useRef} from "react";
import { useDispatchCart,useCart } from "./ContextReducer";
export default function Card(props) {
let dispatch = useDispatchCart(); 
let data = useCart() 
const priceRef  = useRef();      //props
let options=props.options;
let priceOptions = Object.keys(options ?? {})
const [qty,setQty]= useState(1);
const [size,setSize]= useState("");
let foodItem = props.food_items;
// const  handleAddToCart = async()=>{
//         await dispatch ({type: "ADD", id: foodItem._id, name: foodItem.name, price: finalPrice, qty: qty, size: size})
//         console.log(data)
//  }
let finalPrice= qty*parseInt(options[size]);
// useEffect(()=>{
//     setSize(priceRef.current.value)
//  },[])
const handleAddToCart = async () => {
  await dispatch({ type: "ADD", id: foodItem._id, name: foodItem.name, price: finalPrice, qty: qty, size: size });
};

useEffect(() => {
  // This effect runs after the state has been updated by the dispatch
  setSize(priceRef.current.value)
  // console.log('State after dispatch:', fetched_data);
}, [fetched_data]); 
  return (
    <div>
      <div>
        <div
          className="card  mt-2"
          style={{ width: "17rem", maxHeight: "360px" }}
        >
          <img src={props.foodItem.img} className="card-img-top" alt="..."  style={{height:"120px", objectFit:"fill"}}/>
          <div className="card-body">
            <h5 className="card-title">{props.foodItem.name}</h5>
            <div className="card_container w-100">
              <select className="m-2 h-100 w-10 bg-success rounded" onChange={(e)=>setQty(e.target.value)}>
                {Array.from(Array(6), (e, i) => {
                  return (
                    <option key={i + 1} value={i + 1}>
                      {i + 1}
                    </option>
                  );
                })}
              </select>
              <select className="m-2 h-100 bg-success rounded" ref = {priceRef} onChange={(e)=>setSize(e.target.value)}>
                {priceOptions.map((data)=>{
                  return <option key ={data} value = {data}>{data}</option>
                })}

              </select>
              <div className="d-inline h-100 fs-5">â‚¹{finalPrice}/-</div>
            </div>
            <hr />
            <button className= {`btn btn-success justify-center ms-2`} onClick={handleAddToCart}>Add to Cart</button>
          </div>
        </div>
      </div>
    </div>
  );
}
