import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import Card from "../components/Card";
import Carousal from "../components/carousal";

export default function Home() {
  const [foodCat, setFoodCat] = useState([]);
  const [foodItem, setFoodItem] = useState([]);

  const loadData = async () => {
    let response = await fetch("http://localhost:4000/api/foodData", {
      method: "POST",
      headers: {
        'Content-Type': 'application/json'
      }
    });
    response = await response.json();
    setFoodItem(response[0]);
    setFoodCat(response[1]);
  }

  useEffect(() => {
    loadData()
  }, []);

  return (
    <div>
      <div><Navbar /></div>
      <div><Carousal /></div>
      <div className='container'>
        {
          foodCat && foodCat.length > 0
            ? foodCat.map((data) => {
              return (
                <div key={data._id}>
                  <div className="fs-3 m-3">{data.CategoryName}</div>
                  <hr />
                  {
                    foodItem && foodItem.length > 0 // Add a check for foodItem before filtering
                      ? foodItem.filter((item) => item.CategoryName === data.CategoryName).map((filterItems) => {
                        return (
                          <div key={filterItems._id}>
                            <Card></Card>
                          </div>
                        )
                      })
                      : <div>No items available for this category.</div>
                  }
                </div>
              )
            })
            : <div>No food categories available.</div>
        }
      </div>
      <div><Footer /></div>
    </div>
  );
}
