import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import Card from "../components/Card";
import Carousal from "../components/Carousal";


export default function Home() {
  const [search, setSearch] = useState('');

  const [foodCat, setFoodCat] = useState([]);
  const [foodItem, setFoodItem] = useState([]);

  const loadData = async () => {
    const response = await fetch("http://localhost:4000/api/foodData");

    const data = await response.json();
    setFoodItem(data.foodItems);
    setFoodCat(data.foodCategory);
    // console.log(data)
  };

  useEffect(() => {
    loadData();
  }, []);

  return (
    <div>
      <div>
        <Navbar />
      </div>
      <div>
      </div>
      <div className="m-3">
        {foodCat !== [] ? (
          foodCat.map((fetched_data) => {
            return (<div className="row mb-3">
              <div key={fetched_data._id}>
                <div  className="fs-3 m-3">
                  {fetched_data.CategoryName}
                </div>
                </div>
                <hr />

                {foodItem !== [] ? (
                  foodItem
                    .filter((item) => (item.CategoryName === fetched_data.CategoryName) && (item.name.toLowerCase().includes(search.toLocaleLowerCase())))
                    .map((filterItems) => {
                      return (
                        <div key={filterItems._id} className ="col-12 col-md-6 col-lg-3">
                          <Card foodName = {filterItems.name} 
                          options = {filterItems.options[0]}
                          imgSrc = {filterItems.img}/>

                          {/* </Card> */}
                        </div>
                      );
                    })
                ) : 
                  <div>No such fetched_data found</div>
                }
              </div>

            );
          })
        ) : ""

        }
      </div>
      <div>
        <Footer />
      </div>
    </div>
  );
}
