import React from 'react'
import {Link,useNavigate} from 'react-router-dom'
import Badge from 'react-bootstrap/Badge';
import Modal from '../Modal';
import Cart from '../screens/Cart';
// const [cartView,setCartView]= useState(false)

export default function Navbar() {
  const [cartView,setCartView]= useState(false)
const navigate = useNavigate();

const handleLogout = ()=>{
    localStorage.removeItem("authauthToken");
    navigate("/login")

}
  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-dark bg-success"> 
  <div className="container-fluid">
    <Link className="navbar-brand fs-0.5 fst-italic" to="/"><b>GoFood</b></Link>
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarNav">
      <ul className="navbar-nav me-auto mb-2">
        <li className="nav-item">
          <Link className="nav-link active fs-5" aria-current="page" to="/"><b>Home</b></Link>
        </li>
        {(!localStorage.getItem("authauthToken"))?
          <li className="nav-item">
          <Link className="nav-link active fs-5" aria-current="page" to="/"><b>My Orders</b></Link>
        </li>

      :""}

      </ul>
      {(localStorage.getItem("authauthToken"))?
      <div className='d-flex'>
        <Link className="btn bg-white text-success mx-1" to="/login">Login</Link>
        <Link className="btn bg-white text-success mx-1" to="/creatuser">signUp</Link>


      </div>

      :<div>
        <div className="btn bg-white text-success mx-2" onClick = {()=>{setCartView(true)}}>My Cart {" "}
        <Badge pill bg= "danger">2</Badge>
        </div>
        {cartView?<Modal onClose = {()=>setCartView(false) }><Cart/></Modal>:null}
        <div className="btn bg-white text-danger mx-2" onClick={handleLogout}>Logout</div>
      </div>
      }
    </div>
  </div>
</nav>
    </div>
  )
}
